    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Les utilisateurs | QuickEat{% endblock %}

{% block body %}
<div class=\"container my-5\">
    <div class=\"d-flex justify-content-center shadow p-3 my-5 bg-warning rounded\">
        <h1>Liste des utilisateurs</h1>
    </div>
    <table class=\"table\">
        <thead>
            <tr>
                <th>Id</th>
                <th>Email</th>
                <th>Nom</th>
                <th>Prénom</th>
                <th>Numéro de téléphone</th>
                <th>Rôle(s)</th>
                <th>Actions</th>
            </tr>
        </thead>
        <tbody>
        {% for user in users %}
            <tr>
                <td>{{ user.id }}</td>
                <td>{{ user.email }}</td>
                <td>{{ user.name }}</td>
                <td>{{ user.firstname }}</td>
                <td>{{ user.phoneNumber }}</td>
                <td><span class=\"badge badge-warning\">{{ user.roles ? user.roles | json_encode : '' }}</span></td>
                <td>
                    <a href=\"{{ path('user_show', {'id': user.id}) }}\" class=\"btn btn-primary\">Voir</a>
                    <a href=\"{{ path('user_edit', {'id': user.id}) }}\" class=\" btn btn-secondary\">Modifer</a>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"11\">Pas d'infos</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>
    <a href=\"{{ path('admin') }}\" class=\"btn btn-primary\">Retour</a>
    <a href=\"{{ path('user_new') }}\" class=\"btn btn-success\">Nouveau</a>
</div>
{% endblock %}
", "user/index.html.twig", "/Users/sbo/Documents/Projet web/Quickeat/templates/user/index.html.twig");
